import {
  computed,
  DestroyRef,
  effect,
  ElementRef,
  inject,
  Injectable,
  Signal,
  signal,
  untracked,
} from '@angular/core';
import { Carousel, Slide, SnapDom } from './models/carousel.model';
import {
  calculateTranslateValueFromIndex,
  extractVisibleSlides,
} from './helpers/calculations.helper';

@Injectable()
export class CarouselStore {
  // All initial values.
  private readonly _state = signal<Carousel>({
    isProjected: false,
    hasReachedEnd: false,
    hasReachedStart: false,
    scrollWidth: 0,
    velocity: 0,
    fullWidth: 0,
    slidesIndexOrder: [],
    slidesWidths: [],
    allSlides: undefined,
    slidesElements: [] as ElementRef<HTMLElement>[],
    snapsDom: [],
    visibleDom: [],
    slides: [],
    slidesPerView: 1,
    spaceBetween: 0,
    showControls: true,
    alwaysShowControls: false,
    iconSize: 50,
    initialSlide: 0,
    freeMode: true,
    mouseWheel: false,
    deltaPosition: 0.6,
    showProgress: true,
    dotsControl: true,
    rewind: false,
    loop: false,
    center: false,
    notCenterBounds: false,
    slideOnClick: true,
    resistance: true,
    currentPosition: -1,
    totalSlidesVisible: 0,
    totalSlides: 0,
    uniqueCarouselId: '',
    currentTranslate: 0,
    currentRealPosition: 0,
    lastTranslate: 0,
    slideTranslates: [],
    minTranslate: 0,
    maxTranslate: 0,
    marginStart: 0,
    marginEnd: 0,
    firstSlideAnchor: 0,
    lastSlideAnchor: 0,
    stepSlides: 1,
  });

  // Final state with all updated values.
  public readonly state: Signal<Carousel> = computed(() => ({
    ...this._state(),
    fullWidth: this.fullWidth(),
    scrollWidth: this.scrollWidth(),
    maxTranslate: this.maxTranslate(),
    minTranslate: this.minTranslate(),
    snapsDom: this.snapsDom(),
    visibleDom: this.visibleDom(),
    slideTranslates: this.slideTranslates(),
    slidesWidths: this.slidesWidths(),
    totalSlides: this.totalSlides(),
    totalSlidesVisible: this.totalSlidesVisible(),
    firstSlideAnchor: this.firstSlideAnchor(),
    lastSlideAnchor: this.lastSlideAnchor(),
  }));

  setCurrentPosition(newPos: number) {
    if (newPos !== this.currentPosition()) {
      this.patch({ currentPosition: newPos });
    }
  }

  // Expose common values.
  // This values are calculated and updated from carousel component (inputs or dom).
  readonly currentRealPosition = computed(
    () => this._state().currentRealPosition
  );
  readonly currentPosition = computed(() => this._state().currentPosition);
  readonly hasReachedStart = computed(() => this._state().hasReachedStart);
  readonly hasReachedEnd = computed(() => this._state().hasReachedEnd);
  readonly currentTranslate = computed(() => this._state().currentTranslate);
  readonly lastTranslate = computed(() => this._state().lastTranslate);
  readonly allSlides = computed(() => this._state().allSlides);
  readonly slides = computed(() => this._state().slides);
  readonly slidesIndexOrder = computed(() => this._state().slidesIndexOrder);
  readonly slidesElements = computed(() => this._state().slidesElements);

  // Computed, need to update manually in state.
  readonly fullWidth = computed(
    () => this._state().allSlides?.nativeElement?.clientWidth ?? 0
  );
  readonly scrollWidth = computed(
    () => this._state().allSlides?.nativeElement?.scrollWidth
  );
  readonly maxTranslate = computed(() => {
    const maxTranslate =
      -(this.scrollWidth() - this.fullWidth()) - this._state().marginEnd;
    if (this._state().center && !this._state().notCenterBounds) {
      return maxTranslate - this.fullWidth() / 2 - this.slidesWidths()[0] / 2;
    }
    return maxTranslate;
  });
  readonly minTranslate = computed(() => {
    if (this._state().center && !this._state().notCenterBounds) {
      return this.fullWidth() / 2 - this.slidesWidths()[0] / 2;
    }
    return this._state().marginStart;
  });
  readonly totalSlides = computed(() => {
    if (this._state().slides && this._state().slides.length) {
      return this._state().slides.length;
    }
    return this._state().slidesElements.length;
  });
  readonly totalSlidesVisible = computed(() => {
    return this.lastSlideAnchor() - this.firstSlideAnchor() + 1;
  });
  readonly lastSlideAnchor = computed(() => {
    if (this.totalSlides()) {
      if (this._state().loop) {
        return this.totalSlides() - 1;
      }

      if (this._state().center) {
        if (
          this._state().notCenterBounds &&
          this._state().slidesPerView !== 'auto'
        ) {
          return (
            this.totalSlides() -
            Math.floor((this._state().slidesPerView as number) / 2)
          );
        } else if (!this._state().notCenterBounds) {
          return this.totalSlides() - 1;
        }
      } else {
        if (this._state().slidesPerView !== 'auto') {
          return (
            this.totalSlides() -
            Math.floor(this._state().slidesPerView as number)
          );
        }

        let index = this.totalSlides() - 1;
        let total = 0;
        let count = 0;
        while (
          index >= 0 &&
          total + this._state().marginEnd < this.fullWidth()
        ) {
          total += this.slidesWidths()[index];
          count++;
          index--;
        }
        return this.totalSlides() - count + 1;
      }
    }
    return 0;
  });
  readonly firstSlideAnchor = computed(() => {
    this.totalSlides();
    if (this._state().center) {
      if (this._state().notCenterBounds) {
        if (this._state().slidesPerView !== 'auto') {
          return Math.floor((this._state().slidesPerView as number) / 2);
        }
        // TODO;
        return 0;
      }
      return 0;
    }
    return 0;
  });
  readonly slidesWidths = computed(() => {
    const slidesWidths = [];
    for (const slide of this.slidesElements()) {
      if (slide.nativeElement.getBoundingClientRect) {
        const slideWidth = slide.nativeElement.getBoundingClientRect().width;
        slidesWidths.push(slideWidth);
      }
    }
    return slidesWidths;
  });

  readonly snapsDom = computed(() =>
    this.slidesIndexOrder().map((logicalIndex, domIndex) => {
      const translate = this.slideTranslates()[logicalIndex];
      const left = Math.abs(translate);
      const width = this.slidesWidths()[logicalIndex] ?? 0;
      return { domIndex, logicalIndex, left, width, translate };
    })
  );
  readonly visibleDom = computed(() =>
    extractVisibleSlides(
      this.snapsDom(),
      this.currentTranslate(),
      this.fullWidth()
    )
  );
  readonly slideTranslates = computed(() => {
    const snaps: number[] = [];

    for (let i = 0; i < this.totalSlides(); i++) {
      const pos = calculateTranslateValueFromIndex(i, {
        ...this._state(),
        slidesIndexOrder: this.slidesIndexOrder(),
        slidesWidths: this.slidesWidths(),
        totalSlides: this.totalSlides(),
      });
      snaps.push(pos);
    }
    return snaps;
  });

  patch(partial: Partial<Carousel>) {
    this._state.update((curr) => ({ ...curr, ...partial }));
  }

  constructor() {
    effect(() => {
      const state = this.state();
      console.log('------- State udated:', { ...state });
    });
  }
}

import { inject, Injectable, Injector, Renderer2 } from '@angular/core';
import { CarouselStore } from '../carousel.store';
import { positiveModulo } from '../helpers/utils.helper';
import {
  CAROUSEL_VIEW,
  CarouselViewActions,
} from '../components/carousel/view-adapter';
import { calculateTranslateValueFromIndex } from '../helpers/calculations.helper';

@Injectable()
export class CarouselTransformService {
  private store = inject(CarouselStore, { self: true });
  private injector = inject(Injector);

  private get view(): CarouselViewActions {
    return this.injector.get(CAROUSEL_VIEW);
  }
  constructor() {}

  public getNewPositionFromTranslate(velocity = false) {
    if (!this.store.state().loop) {
      this.store.patch({
        currentTranslate: Math.max(
          this.store.state().maxTranslate,
          Math.min(
            this.store.currentTranslate() +
              (velocity ? this.store.state().velocity : 0),
            this.store.state().minTranslate
          )
        ),
      });
    } else {
      this.store.patch({
        currentTranslate:
          this.store.currentTranslate() +
          (velocity ? this.store.state().velocity : 0),
      });
    }

    let position =
      Math.abs(
        this.store.currentTranslate() - this.store.state().minTranslate
      ) /
      (this.store.slidesWidths()[0] + this.store.state().spaceBetween);

    position = this.getRealPositionFromExactPosition(position);

    const isNewPosition =
      Math.abs(this.store.currentPosition() - position) >
      this.store.state().deltaPosition;

    return {
      exactPosition: position,
      position: isNewPosition ? Math.round(position) : undefined,
    };
  }

  public getRealPositionFromExactPosition(position: number) {
    const roundPosition = Math.round(position);
    const realPosition = this.store.slidesIndexOrder()[roundPosition];
    // We keep exact decimal part of position and add it to real position.
    const decimalPart = position - roundPosition;
    position = realPosition + decimalPart;
    return position;
  }

  /**
   * Get posX from slide index position.
   * @param index
   * @returns
   */
  public getTranslateFromPosition(
    index = this.store.currentRealPosition()
  ): number {
    const realIndexFromPosition = this.store
      .slidesIndexOrder()
      .findIndex((element) => element === index);

    // For centering without centering at bounds.
    if (this.store.state().center && this.store.state().notCenterBounds) {
      const isBefore =
        realIndexFromPosition <
        (this.store.state().slidesPerView as number) / 2;
      const isAfter =
        realIndexFromPosition >
        this.store.state().totalSlides -
          (this.store.state().slidesPerView as number) / 2;
      if (isBefore || isAfter) {
        return isBefore
          ? this.store.state().minTranslate
          : this.store.state().maxTranslate;
      }

      const centerValue =
        this.store.state().fullWidth / 2 -
        this.store.slidesWidths()[realIndexFromPosition] / 2;

      const posX = this.calculateTranslateValueFromIndex(index) + centerValue;
      return this.clampTranslateValue(posX);
    }

    // Calculate position from slides widths and space between.
    const posX = this.calculateTranslateValueFromIndex(index);

    if (this.store.state().loop) {
      // Authorize any translation without restriction.
      return posX;
    }

    // If not loop, we restrict translation to min and max.
    return this.clampTranslateValue(posX);
  }

  private calculateTranslateValueFromIndex(index: number) {
    return calculateTranslateValueFromIndex(index, this.store.state());
  }

  private clampTranslateValue(posX: number) {
    return Math.max(
      this.store.state().maxTranslate,
      Math.min(posX, this.store.state().minTranslate)
    );
  }

  public calculateInitialTranslations() {
    const realInitialSlide = this.store.state().initialSlide;
    const currentIndex =
      this.store.currentPosition() != -1 &&
      this.store.currentPosition() !== realInitialSlide
        ? this.store.currentPosition()
        : realInitialSlide;
    if (this.store.state().center) {
      //@todo issue with initialslide
      const translateToApply = currentIndex
        ? this.getTranslateFromPosition(currentIndex)
        : this.store.minTranslate();
      this.view.updateTransform(translateToApply, false);
    } else {
      this.view.updateTransform(
        this.getTranslateFromPosition(currentIndex),
        false
      );
    }
  }
}

