import { CommonModule } from '@angular/common';
import {
  Component,
  EventEmitter,
  OnInit,
  Input,
  Output,
  computed,
  input,
} from '@angular/core';

type PaginationType = 'number' | 'dot' | 'dynamic_dot' | 'fraction';
export interface Pagination {
  type: PaginationType;
  clickable?: boolean;
  external?: boolean;
}

@Component({
    imports: [CommonModule],
    selector: 'app-pagination',
    templateUrl: './pagination.component.html',
    styleUrls: ['./pagination.component.scss']
})
export class PaginationComponent {
  @Input() pagination!: Pagination;
  @Input() currentPosition: number = 0;
  @Input() firstSlideAnchor: number = 0;
  @Input() lastSlideAnchor: number = 0;
  @Input() totalSlides: number = 0;
  totalSlidesVisible = input(0);

  @Output() goToSlide: EventEmitter<number> = new EventEmitter<number>();

  public readonly dotWidth = 0.5;

  constructor() {}

  public readonly maxWidth = computed(() => {
    if (this.totalSlidesVisible() >= 5) {
      return 5 * 0.5 + 4 * 1;
    }

    if (this.totalSlidesVisible() === 4) {
      return 4 * 0.5 + 3 * 1;
    }

    return 3 * 0.5 + 2 * 1;
  });

  public slideTo(slide: number) {
    this.goToSlide.emit(slide);
  }

  get range(): number[] {
    return Array.from({ length: this.totalSlidesVisible() }, (_, i) => i + 1);
  }
  get currentPositionVisible(): number {
    const realPaginationPosition = Math.max(
      0,
      this.currentPosition - this.firstSlideAnchor
    );
    return Math.min(realPaginationPosition, this.totalSlidesVisible() - 1);
  }
  get dotLeftPosition(): number {
    // maxwidth: 5 * 0.5rem + 4 * 1rem = 6.5

    // Default case : totalSlides >= 5
    // * - -   - * - -   - - * - -   - - * -  - - *
    if (this.totalSlidesVisible() >= 5) {
      return (
        (this.maxWidth() -
          (this.currentPositionVisible * (this.maxWidth() - 0.5)) / 2 -
          0.5) /
        2
      );
    }
    // totalSlides = 4
    // maxwidth: 5
    // * - -   - * - -   - - * -   - - *
    // @todo
    if (this.totalSlidesVisible() === 4) {
      const middle = this.maxWidth() / 2;
      const firstLeft = middle - this.dotWidth / 2;
      const step = 1.5;
      return firstLeft - step * this.currentPositionVisible;
      // (
      //   (this.maxWidth() -
      //     (this.currentPositionVisible * (this.maxWidth() - 0.5)) / 2 -
      //     0.5) /
      //   2
      // );
    }

    // totalSlides = 3
    // maxWidth: 3.5
    //  * -    - * -    - *
    if (this.totalSlidesVisible() === 3) {
      return (
        (this.maxWidth() -
          this.currentPositionVisible * (this.maxWidth() - 0.5) -
          0.5) /
        2
      );
    }

    return 0;
  }
}

styleUrlsData.data:
.dot-pagination {
  &.clickable {
    .dot {
      cursor: pointer;
    }
  }

  .dot {
    opacity: 0.5;
    width: 0.5rem;
    height: 0.5rem;
    background-color: grey;
    //border: 1px solid grey;
    border-radius: 50%;

    &.selected {
      background-color: darkblue;
      transform: scale(1.5);
    }
  }

  &.pagination-dot {
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: center;
    gap: 1rem;
    padding: 1rem;
  }

  &.dynamic {
    display: flex;
    flex-direction: row;
    gap: 1rem;
    overflow: hidden;

    .dot {
      position: relative;
      transition: left 0.2s;
      flex: 0 0 auto;
      transform: scale(1);

      &.next,
      &.prev {
        transform: scale(0.6);
      }

      &.next-next,
      &.prev-prev {
        transform: scale(0.4);
      }
    }
  }
}

components.templateData:
@if (pagination.type === "dot") {
<div
  class="dot-pagination pagination-dot"
  [class.clickable]="pagination.clickable"
>
  @for (num of range; track $index) {
  <span
    (click)="pagination.clickable ? slideTo(num - 1) : null"
    class="dot"
    [class.selected]="currentPosition === num - 1"
  ></span>
  }
</div>
} @else if (totalSlidesVisible() > 1 && pagination.type === "dynamic_dot") {
<div
  class="dot-pagination dynamic"
  [style.width.rem]="maxWidth()"
  [class.clickable]="pagination.clickable"
>
  @for (num of range; track $index) {
  <span
    (click)="pagination.clickable ? slideTo(num - 1) : null"
    class="dot"
    [style.left.rem]="dotLeftPosition"
    [class.selected]="currentPositionVisible === num - 1"
    [class.next]="currentPositionVisible === num - 2"
    [class.next-next]="currentPositionVisible === num - 3"
    [class.prev]="currentPositionVisible === num"
    [class.prev-prev]="currentPositionVisible === num + 1"
  ></span>
  }
</div>
} @else if (pagination.type === "fraction") {
<div>
  <span>{{ currentPosition + 1 }} / {{ totalSlides }}</span>
</div>
}

