export interface AxisConfig {
  // DOM
  getContainerSize(el?: HTMLElement): number; // width or height
  getScrollSize(el?: HTMLElement): number; // scrollWidth / scrollHeight
  rectStart(rect: DOMRect): number; // left / top
  rectSize(el?: HTMLElement): number; // width / height

  // Pointers / drag
  pointerMainPos(pos: { x: number; y: number }): number; // x or y
  pointerCrossPos(pos: { x: number; y: number }): number; // y or x

  // Transform CSS for slides
  slidesTransform(translate: number): string;

  // Padding “peek” for container
  peekPadding(peek: number): Partial<CSSStyleDeclaration>;

  // Event Mouse
  mouseMainPos(event: MouseEvent): number;
  mouseCrossPos(event: MouseEvent): number;
  // Event Wheel
  wheelMainDelta(event: WheelEvent): number;
  wheelCrossDelta(event: WheelEvent): number;
}

export const HORIZONTAL_AXIS_CONFIG: AxisConfig = {
  getContainerSize: (el) =>
    el?.getBoundingClientRect?.()?.width ?? el?.clientWidth ?? 0,
  getScrollSize: (el) => el?.scrollWidth ?? 0,
  rectStart: (r) => r.left,
  rectSize: (el) => el?.getBoundingClientRect?.().width ?? 0,
  pointerMainPos: (p) => p.x,
  pointerCrossPos: (p) => p.y,
  slidesTransform: (t) => `translate3d(${t}px, 0, 0)`,
  peekPadding: (peek) => ({
    paddingLeft: `${peek}px`,
    paddingRight: `${peek}px`,
    paddingTop: '',
    paddingBottom: '',
  }),
  mouseMainPos: (event) => event.pageX,
  mouseCrossPos: (event) => event.pageY,
  wheelMainDelta: (event) => event.deltaX || event.deltaY,
  wheelCrossDelta: (event) => event.pageY,
};

export const VERTICAL_AXIS_CONFIG: AxisConfig = {
  getContainerSize: (el) =>
    el?.getBoundingClientRect?.()?.height ?? el?.clientHeight ?? 0,
  getScrollSize: (el) => el?.scrollHeight ?? 0,
  rectStart: (r) => r.top,
  rectSize: (el) => el?.getBoundingClientRect?.().height ?? 0,
  pointerMainPos: (p) => p.y,
  pointerCrossPos: (p) => p.x,
  slidesTransform: (t) => `translate3d(0, ${t}px, 0)`,
  peekPadding: (peek) => ({
    paddingTop: `${peek}px`,
    paddingBottom: `${peek}px`,
    paddingLeft: '',
    paddingRight: '',
  }),
  mouseMainPos: (event) => event.pageY,
  mouseCrossPos: (event) => event.pageX,
  wheelMainDelta: (event) => event.deltaY,
  wheelCrossDelta: (event) => event.pageX,
};

import { SafeHtml } from '@angular/platform-browser';
import { Pagination, SlideDirective } from '../../public-api';
import { ElementRef } from '@angular/core';

export const TRANSITION_DURATION = 400; // ms

export type CarouselAxis = 'horizontal' | 'vertical';
export type CarouselDirection = 'ltr' | 'rtl';

export type Slide = {
  image: string;
  id?: string;
  disabled?: boolean;
};

export type AutoplayOptions = {
  delay?: number; // ms entre 2 avances (par défaut 2500)
  pauseOnHover?: boolean; // pause quand la souris est dessus
  pauseOnFocus?: boolean; // pause quand un élément interne a le focus
  stopOnInteraction?: boolean; // stop définitif si l’utilisateur interagit (flèche, drag, dot…)
  disableOnHidden?: boolean; // stop quand l’onglet n’est pas visible
  resumeOnMouseLeave?: boolean; // relance à la sortie si pas stop
};

export interface CarouselResponsiveConfig {
  [mediaQuery: string]: {
    slidesPerView?: number;
    spaceBetween?: number;
  };
}

export type PeekEdges =
  | undefined
  | {
      absoluteOffset?: number;
      relativeOffset?: number;
    };

export interface SnapDom {
  domIndex: number;
  logicalIndex: number;
  left: number;
  width: number;
  translate: number;
}

export interface Carousel {
  isProjected: boolean;
  snapsDom: SnapDom[];
  visibleDom: SnapDom[];
  resistance: boolean;
  slides: Slide[];
  slidesElements: ElementRef<HTMLElement>[];
  slidesPerView: number | 'auto';
  spaceBetween: number;
  showControls: boolean;
  alwaysShowControls: boolean;
  iconSize: number;
  pagination?: Pagination;
  initialSlide: number;
  freeMode: boolean;
  mouseWheel:
    | boolean
    | {
        horizontal?: boolean;
        vertical?: boolean;
      };
  deltaPosition: number;
  showProgress: boolean;
  dotsControl: boolean;
  rewind: boolean;
  loop: boolean;
  center: boolean;
  notCenterBounds: boolean;
  slideOnClick: boolean;

  marginEnd: number;
  marginStart: number;

  lazyLoading: boolean;

  breakpoints?: CarouselResponsiveConfig;

  customStyle?: SafeHtml;

  currentPosition: number;

  hasReachedEnd: boolean;

  hasReachedStart: boolean;

  totalSlidesVisible: number;

  totalSlides: number;

  fullWidth: number;

  scrollWidth: number;

  currentTranslate: number;
  currentRealPosition: number;
  lastTranslate: number;
  minTranslate: number;
  maxTranslate: number;

  slidesWidths: number[];

  uniqueCarouselId: string;

  allSlides: ElementRef<any> | undefined;

  slidesIndexOrder: number[];

  slideTranslates: number[];

  velocity: number;

  firstSlideAnchor: number;
  lastSlideAnchor: number;

  stepSlides: number;

  autoplay: boolean | AutoplayOptions;

  draggable: boolean;

  canSwipe: boolean;

  peekEdges: PeekEdges;

  dragIgnoreSelector?: string;

  keyboardNavigation: boolean;

  navigateSlideBySlide: boolean;

  thumbsOptions:
    | {
        selectionBar: boolean;
      }
    | undefined;

  projectedSlides?: SlideDirective[];

  direction: CarouselDirection;
  axis: CarouselAxis;
}

export const CAROUSEL_SLIDE_CLASS = 'slide';

export const THUMBS_TRANSITION_DURATION_MS = 200;

