import { inject, Injectable, DestroyRef } from '@angular/core';
import { CarouselStore } from '../carousel.store';

interface InertiaConfig {
  baseFriction: number;
  edgeFriction: number;
  minVelocity: number;
}

@Injectable()
export class CarouselPhysicsService {
  private readonly store = inject(CarouselStore);
  private readonly destroyRef = inject(DestroyRef);

  private animationFrameId?: number;

  private readonly DEFAULT_CONFIG: InertiaConfig = {
    baseFriction: 0.93,
    edgeFriction: 0.8,
    minVelocity: 0.5,
  };

  constructor() {
    this.destroyRef.onDestroy(() => this.cancel());
  }

  applyInertia(
    config: InertiaConfig = this.DEFAULT_CONFIG,
    onUpdate?: (translate: number) => void
  ) {
    this.cancel();

    const step = () => {
      const velocity = this.store.state().velocity;

      if (Math.abs(velocity) < config.minVelocity) {
        return;
      }

      const friction = this.isAtEdge()
        ? config.edgeFriction
        : config.baseFriction;
      const newVelocity = velocity * friction;
      const newTranslate = this.store.currentTranslate() + velocity;

      const clampedTranslate = Math.max(
        this.store.state().maxTranslate,
        Math.min(newTranslate, this.store.state().minTranslate)
      );

      this.store.patch({
        currentTranslate: clampedTranslate,
        velocity: newVelocity,
      });

      onUpdate?.(clampedTranslate);

      this.animationFrameId = requestAnimationFrame(step);
    };

    this.animationFrameId = requestAnimationFrame(step);
  }

  cancel() {
    if (this.animationFrameId) {
      cancelAnimationFrame(this.animationFrameId);
      this.animationFrameId = undefined;
    }
  }

  private isAtEdge(): boolean {
    const translate = this.store.currentTranslate();
    return (
      translate <= this.store.state().maxTranslate ||
      translate >= this.store.state().minTranslate
    );
  }
}

import { CommonModule } from '@angular/common';
import {
  Component,
  EventEmitter,
  OnInit,
  Input,
  Output,
  computed,
  input,
  inject,
} from '@angular/core';
import { CarouselStore } from '../../carousel.store';

type PaginationType = 'number' | 'dot' | 'dynamic_dot' | 'fraction';
export interface Pagination {
  type: PaginationType;
  clickable?: boolean;
  external?: boolean;
}

@Component({
  imports: [CommonModule],
  selector: 'app-pagination',
  templateUrl: './pagination.component.html',
  styleUrls: ['./pagination.component.scss'],
  host: {
    '[class.vertical]': 'this.store.isVertical()',
  },
})
export class PaginationComponent {
  public readonly store = inject(CarouselStore);

  @Output() goToSlide: EventEmitter<number> = new EventEmitter<number>();

  public readonly dotWidth = 0.5;

  constructor() {}

  public readonly maxSpan = computed(() => {
    if (this.store.totalSlidesVisible() >= 5) {
      return 5 * 0.5 + 4 * 1;
    }

    if (this.store.totalSlidesVisible() === 4) {
      return 4 * 0.5 + 3 * 1;
    }

    return 3 * 0.5 + 2 * 1;
  });

  public slideTo(slide: number) {
    this.goToSlide.emit(slide);
  }

  public readonly range = computed(() =>
    Array.from({ length: this.store.totalSlidesVisible() }, (_, i) => i + 1)
  );
  public readonly currentPositionVisible = computed(() => {
    const realPaginationPosition = Math.max(
      0,
      this.store.currentPosition() - this.store.firstSlideAnchor()
    );
    return Math.min(
      realPaginationPosition,
      this.store.totalSlidesVisible() - 1
    );
  });

  private computedMainOffset() {
    // maxwidth: 5 * 0.5rem + 4 * 1rem = 6.5

    // Default case : totalSlides >= 5
    // * - -   - * - -   - - * - -   - - * -  - - *
    if (this.store.totalSlidesVisible() >= 5) {
      return (
        (this.maxSpan() -
          (this.currentPositionVisible() * (this.maxSpan() - this.dotWidth)) /
            2 -
          this.dotWidth) /
        2
      );
    }
    // totalSlides = 4
    // maxwidth: 5
    // * - -   - * - -   - - * -   - - *
    // @todo
    if (this.store.totalSlidesVisible() === 4) {
      const middle = this.maxSpan() / 2;
      const firstLeft = middle - this.dotWidth / 2;
      const step = 1.5;
      return firstLeft - step * this.currentPositionVisible();
      // (
      //   (this.maxWidth() -
      //     (this.currentPositionVisible * (this.maxWidth() - 0.5)) / 2 -
      //     0.5) /
      //   2
      // );
    }

    // totalSlides = 3
    // maxWidth: 3.5
    //  * -    - * -    - *
    if (this.store.totalSlidesVisible() === 3) {
      return (
        (this.maxSpan() -
          this.currentPositionVisible() * (this.maxSpan() - this.dotWidth) -
          this.dotWidth) /
        2
      );
    }

    return 0;
  }

  public readonly dotMainOffset = computed(() => {
    const offset = this.computedMainOffset();
    return this.store.isRtl() ? -offset : offset;
  });
}

