import { Injectable, NgZone, inject } from '@angular/core';
import { DOCUMENT } from '@angular/common';
import { CarouselStore } from '../carousel.store';

type CarouselBreakpoints = Record<string, any>;

@Injectable()
export class CarouselBreakpointService {
  private readonly store = inject(CarouselStore);
  private readonly ngZone = inject(NgZone);
  private readonly document = inject(DOCUMENT);
  private readonly window: Window | null = this.document?.defaultView ?? null;

  private mediaQueryListeners: {
    mql: MediaQueryList;
    listener: (e: MediaQueryListEvent) => void;
  }[] = [];

  /**
   * Generate CSS to apply breakpoints on SSR.
   */
  generateCss(
    breakpoints: CarouselBreakpoints | undefined,
    uniqueCarouselId: string
  ): string {
    if (!breakpoints || this.store.slidesPerView() === 'auto') {
      return '';
    }

    let css = '<style>';
    const baseSlidesPerView = this.store.slidesPerView() as number;
    const baseSpaceBetween = this.store.spaceBetween();

    Object.keys(breakpoints).forEach((query) => {
      const config = breakpoints[query] ?? {};
      const slidesPerView: number = config.slidesPerView ?? baseSlidesPerView;
      const spaceBetween: number = config.spaceBetween ?? baseSpaceBetween;

      const columns = this.getGridColumnsValue(slidesPerView, spaceBetween);

      css += `
        @media ${query} {
          .slides.${uniqueCarouselId} {
            grid-auto-columns: ${columns} !important;
            gap: ${spaceBetween}px !important;
          }
        }
      `;
    });

    css += '</style>';
    return css;
  }

  /**
   * Create matchMedia listeners for each breakpoint.
   */
  setupMediaQueryListeners(
    breakpoints: CarouselBreakpoints | undefined,
    onMatch: (config: any) => void
  ): void {
    if (!this.window || !breakpoints) {
      return;
    }

    this.clear();

    Object.keys(breakpoints).forEach((query) => {
      const mql = this.window!.matchMedia(query);
      const config = breakpoints[query];

      const listener = (e: MediaQueryListEvent) => {
        this.ngZone.run(() => {
          if (e.matches) {
            onMatch(config);
          }
        });
      };

      mql.addEventListener('change', listener);
      this.mediaQueryListeners.push({ mql, listener });
    });
  }

  /**
   * Remove listeners.
   */
  clear(): void {
    this.mediaQueryListeners.forEach(({ mql, listener }) => {
      if (mql.removeEventListener) {
        mql.removeEventListener('change', listener);
      }
    });
    this.mediaQueryListeners = [];
  }

  private getGridColumnsValue(
    slidesPerView: number,
    spaceBetween: number
  ): string {
    return `calc((100% - ${
      spaceBetween * (slidesPerView - 1)
    }px) / ${slidesPerView})`;
  }
}

import { ElementRef, inject, Injectable, Renderer2 } from '@angular/core';
import { CarouselStore } from '../carousel.store';

export const DEFAULT_IMAGES_TO_LOAD = 6;

@Injectable()
export class CarouselDomService {
  private store = inject(CarouselStore);
  constructor(private renderer: Renderer2) {}

  /**
   * Add class to identify slides roles and states.
   * @returns
   */
  updateSlides() {
    if (!this.store.allSlides()) {
      return;
    }

    if (this.store.slidesElements()) {
      this.store
        .slidesElements()
        ?.forEach((slide: ElementRef<HTMLElement>, index: number) => {
          this.resetPositions(slide.nativeElement, index);
          this.setAccessibility(slide.nativeElement, index);
          this.setLazyLoading(slide.nativeElement, index);
        });

      this.setSlidesPositions();
    }
  }

  private setSlidesPositions() {
    const curr = this.store
      .allSlides()
      ?.nativeElement.querySelector(
        '.slide.position-' + (this.store.currentPosition() + 1)
      );
    const prev = this.store
      .allSlides()
      ?.nativeElement.querySelector(
        '.slide.position-' +
          (this.store.currentPosition() <= 0
            ? this.store.state().loop
              ? this.store.totalSlides()
              : null
            : this.store.currentPosition())
      );
    const next = this.store
      .allSlides()
      ?.nativeElement.querySelector(
        '.slide.position-' +
          (this.store.currentPosition() + 2 <= this.store.totalSlides()
            ? this.store.currentPosition() + 2
            : this.store.state().loop
            ? 1
            : null)
      );
    if (curr) {
      this.renderer.addClass(curr, 'curr');
    }
    if (next) {
      this.renderer.addClass(next, 'next');
    }
    if (prev) {
      this.renderer.addClass(prev, 'prev');
    }
  }

  /**
   * Handled with native loading html.
   * @todo dynamic while sliding ?
   * @param slide
   * @param index
   */
  private setLazyLoading(slide: HTMLElement, index: number) {
    const state = this.store.state();

    if (!state.lazyLoading) {
      return;
    }

    const images = Array.from(slide.querySelectorAll('img'));
    if (!images?.length) {
      return;
    }

    const total = this.store.totalSlides();
    if (!total) {
      return;
    }

    const current = this.store.currentPosition();
    const loop = state.loop;
    const center = state.center;

    const slidesPerView = this.store.slidesPerView();
    const windowSize =
      typeof slidesPerView === 'number'
        ? Math.max(1, Math.ceil(slidesPerView))
        : DEFAULT_IMAGES_TO_LOAD;

    let isEager = false;

    if (!center) {
      if (!loop) {
        const minIndex = current;
        const maxIndex = Math.min(current + windowSize - 1, total - 1);
        isEager = index >= minIndex && index <= maxIndex;
      } else {
        const diff = (index - current + total) % total;
        isEager = diff >= 0 && diff <= windowSize - 1;
      }
    } else {
      const radius = Math.floor(windowSize / 2);
      let diff = Math.abs(index - current);

      if (loop) {
        diff = Math.min(diff, total - diff);
      }

      isEager = diff <= radius;
    }

    images.forEach((image) => {
      this.renderer.setProperty(image, 'loading', isEager ? 'eager' : 'lazy');
    });
  }

  private resetPositions(slide: HTMLElement, index: number) {
    this.renderer.removeClass(slide, 'prev');
    this.renderer.removeClass(slide, 'curr');
    this.renderer.removeClass(slide, 'next');
    this.renderer.addClass(slide, 'position-' + (index + 1));
  }

  /**
   * Add accessibility attributes to slides.
   * @param slide
   * @param index
   */
  private setAccessibility(slide: HTMLElement, index: number) {
    this.renderer.setAttribute(slide, 'role', 'group');
    this.renderer.setAttribute(slide, 'aria-roledescription', 'slide');
    this.renderer.setAttribute(
      slide,
      'aria-label',
      `${index + 1} of ${this.store.totalSlides()}`
    );
    this.renderer.setAttribute(
      slide,
      'tabindex',
      index === this.store.currentPosition() ? '0' : '-1'
    );
  }
}

import { inject, Injectable, Injector, signal, untracked } from '@angular/core';
import { CarouselStore } from '../carousel.store';
import { CarouselLoopService } from './carousel-loop.service';
import { CarouselPhysicsService } from './carousel-physics.service';
import { CarouselDomService } from './carousel-dom.service';
import {
  CAROUSEL_VIEW,
  CarouselViewActions,
} from '../components/carousel/view-adapter';
import { getPointerPosition } from '../helpers/event.helper';

export type PointerCoords = {
  x: number;
  y: number;
  isTouch: boolean;
};

export type DragState = {
  isDragging: boolean;
  hasMoved: boolean;
  hasExtraTranslation: boolean;
  lastMain: number;
  currentMain: number;
  lastMoveTime: number;
  lastClickTime: number;
  lastMainPosition: number;
  lockedAxis: 'main' | 'cross' | null;
};

@Injectable()
export class CarouselInteractionService {
  private readonly store = inject(CarouselStore);
  private readonly physicsService = inject(CarouselPhysicsService);
  private readonly loopService = inject(CarouselLoopService);
  private readonly domService = inject(CarouselDomService);
  private readonly injector = inject(Injector);

  private suppressNextClick = false;

  private get view(): CarouselViewActions {
    return this.injector.get(CAROUSEL_VIEW);
  }

  public sensitivity = 1;
  public velocitySensitivity = 5;
  public velocitySensitivityFreeMode = 1;
  public velocityBounds = 0.5;

  public gestureStart: {
    main: number;
    cross: number;
    time: number;
    event?: MouseEvent | TouchEvent;
  } = { main: 0, cross: 0, time: 0 };

  private dragState = signal<DragState>({
    isDragging: false,
    hasMoved: false,
    hasExtraTranslation: false,
    lastMain: 0,
    currentMain: 0,
    lastMoveTime: 0,
    lastClickTime: 0,
    lastMainPosition: 0,
    lockedAxis: null,
  });

  public getDragState() {
    return this.dragState();
  }

  public updateDragState(updatedState: Partial<DragState>) {
    this.dragState.update((state) => ({
      ...state,
      ...updatedState,
    }));
  }

  /**
   * Update translation as user is dragging.
   * @param deltaMain
   * @param noExtraTranslation
   * @param mainPosition
   */
  public followUserMove(
    deltaMain: number,
    noExtraTranslation = false,
    mainPosition?: number
  ) {
    const effectiveDeltaMain = this.store.isRtl() ? -deltaMain : deltaMain;

    let newTranslate =
      this.store.currentTranslate() + effectiveDeltaMain / this.sensitivity;

    const isOutOfBounds =
      !this.store.loop() &&
      (newTranslate < this.store.maxTranslate() ||
        newTranslate > this.store.minTranslate());

    if (isOutOfBounds) {
      if (!this.store.resistance() || noExtraTranslation) {
        // If we are out of bounds, we don't want to apply extra translation.
        newTranslate = Math.max(
          this.store.maxTranslate(),
          Math.min(newTranslate, this.store.minTranslate())
        );
      } else {
        this.dragState.update((state) => ({
          ...state,
          hasExtraTranslation: true,
        }));
        newTranslate =
          this.store.currentTranslate() +
          (effectiveDeltaMain / this.sensitivity) * this.velocityBounds;
      }
    } else {
      this.dragState.update((state) => ({
        ...state,
        hasExtraTranslation: false,
      }));
    }

    this.updatePositionOnMouseMove(newTranslate, mainPosition);
  }

  /**
   * Update current translate and apply transform CSS.
   * @param newTranslate
   * @param mainPosition
   */
  private updatePositionOnMouseMove(
    newTranslate: number,
    mainPosition?: number
  ) {
    const rawVelocity = mainPosition
      ? (mainPosition - this.dragState().lastMainPosition) *
        (this.store.freeMode()
          ? this.velocitySensitivityFreeMode
          : this.velocitySensitivity)
      : 0;

    const velocity = this.store.isRtl() ? -rawVelocity : rawVelocity;

    this.store.patch({
      currentTranslate: newTranslate,
      velocity,
    });

    this.loopService.insertLoopSlides();

    this.view.updateTransform();
  }

  public resetDrag() {
    this.dragState.update((state) => ({
      ...state,
      isDragging: false,
      hasMoved: false,
      lockedAxis: null,
    }));
    this.domService.updateSlides();
  }

  private shouldStartDrag(event: MouseEvent | TouchEvent): boolean {
    if (!this.store.draggable()) {
      return false;
    }

    const target = event.target as HTMLElement | null;
    if (!target) {
      return true;
    }

    const selector = this.store.state().dragIgnoreSelector;
    if (!selector) {
      return true;
    }

    const ignoreCandidate = target.closest(selector);
    return !ignoreCandidate;
  }

  public handleMove(event: MouseEvent | TouchEvent) {
    const dragState = this.getDragState();

    if (!dragState.isDragging) {
      return;
    }

    const { x, y, isTouch } = getPointerPosition(event);
    const main = this.store.axisConf().pointerMainPos({ x, y });
    const cross = this.store.axisConf().pointerCrossPos({ x, y });

    const now = Date.now();
    const gestureStart = this.gestureStart;
    // Determine threshold for mobile scroll on page while sliding.
    const AXIS_LOCK_THRESHOLD = 8;

    if (isTouch && gestureStart) {
      const { lockedAxis } = dragState;

      if (!lockedAxis) {
        const deltaMain = main - gestureStart.main;
        const deltaCross = cross - gestureStart.cross;
        const absDeltaMain = Math.abs(deltaMain);
        const absDeltaCross = Math.abs(deltaCross);

        // Too little gesture to decide.
        if (
          absDeltaMain < AXIS_LOCK_THRESHOLD &&
          absDeltaCross < AXIS_LOCK_THRESHOLD
        ) {
          return;
        }

        if (absDeltaMain >= absDeltaCross) {
          this.updateDragState({ lockedAxis: 'main' });
        } else {
          // Cross gesture => no drag
          this.resetDrag();
          return;
        }

        // Main gesture.
        this.updateDragState({ lockedAxis: 'main' });
      } else if (lockedAxis === 'cross') {
        return;
      }
    }

    // For mouse.
    if (!isTouch && dragState.lockedAxis !== 'main') {
      this.updateDragState({ lockedAxis: 'main' });
    }

    if (this.getDragState().lockedAxis !== 'main') {
      return;
    }

    if (isTouch) {
      event.preventDefault();
    }

    const deltaMain = (main - dragState.currentMain) * this.sensitivity;

    const currentState = this.getDragState();
    this.updateDragState({
      hasMoved: true,
      lastMoveTime: now,
      currentMain: currentState.currentMain + deltaMain,
      lastMainPosition:
        now - currentState.lastMoveTime > 50
          ? main
          : currentState.lastMainPosition,
    });

    if (this.shouldStartDrag(gestureStart.event ?? event)) {
      this.followUserMove(deltaMain, false, main);
    }
  }

  public handleStart(event: MouseEvent | TouchEvent) {
    const position = getPointerPosition(event);

    const main = this.store.axisConf().pointerMainPos(position);
    const cross = this.store.axisConf().pointerCrossPos(position);

    this.gestureStart = {
      main,
      cross,
      time: Date.now(),
      event,
    };

    this.store.patch({ lastTranslate: this.store.currentTranslate() });

    this.updateDragState({
      isDragging: true,
      hasMoved: false,
      hasExtraTranslation: false,
      currentMain: main,
      lastMainPosition: main,
      lastClickTime: new Date().getTime(),
      lockedAxis: null,
    });
  }

  public handleEnd(event: MouseEvent | TouchEvent) {
    if (!this.getDragState().isDragging) {
      return;
    }

    const { x, y } = getPointerPosition(event);
    const main = this.store.axisConf().pointerMainPos({ x, y });

    const timeEnd = Date.now();

    const dist = main - this.gestureStart.main;
    const duration = timeEnd - this.gestureStart.time;
    const absDist = Math.abs(dist);

    const SWIPE_THRESHOLD = 15; // px
    const SWIPE_TIME_LIMIT = 200; // ms
    const MIN_DRAG_DIST = 5; // px

    // Will be a click, we do nothing.
    if (absDist < MIN_DRAG_DIST && !this.getDragState().hasExtraTranslation) {
      this.resetDrag();
      return;
    }

    if (this.store.draggable()) {
      this.suppressNextClick = true;
    }

    const isSwipe = duration < SWIPE_TIME_LIMIT && absDist > SWIPE_THRESHOLD;

    // Freemode specific
    if (this.store.freeMode() || this.store.navigateSlideBySlide()) {
      if (isSwipe) {
        this.physicsService.applyInertia(undefined, (translate) => {
          this.view.updateTransform(translate);
        });
        this.resetDrag();
        return;
      }
      if (
        this.getDragState().hasExtraTranslation &&
        !this.store.navigateSlideBySlide()
      ) {
        this.view.slideToNearest();
        this.resetDrag();
        return;
      }
      this.resetDrag();
      return;
    }

    const swipeToLeft = dist < 0;

    // Swipe
    if (isSwipe && this.store.canSwipe()) {
      if (!this.store.isRtl()) {
        swipeToLeft ? this.view.slideToNext() : this.view.slideToPrev();
      } else {
        swipeToLeft ? this.view.slideToPrev() : this.view.slideToNext();
      }
      this.resetDrag();
      return;
    }

    // CLassic translation
    if (this.store.draggable()) {
      this.view.slideToNearest();
    }
    this.resetDrag();
  }

  public handleClick(event: MouseEvent) {
    if (this.suppressNextClick) {
      this.suppressNextClick = false;
      return;
    }

    if (this.getDragState().hasMoved) {
      return;
    }

    const CLICK_MAX_DIST = 2; // px
    const CLICK_MAX_TIME = 200; // ms

    const deltaMain =
      this.store.axisConf().mouseMainPos(event) - this.gestureStart.main;
    const deltaCross =
      this.store.axisConf().mouseCrossPos(event) - this.gestureStart.cross;
    const dist = Math.hypot(deltaMain, deltaCross);
    const dt = Date.now() - this.gestureStart.time;

    if (dist > CLICK_MAX_DIST || dt > CLICK_MAX_TIME) {
      return;
    }

    this.view.clickOnSlide(event);
  }

  public handleWheel(event: WheelEvent) {
    const mouseWheel = this.store.state().mouseWheel;
    if (!mouseWheel) {
      return;
    }
    const wheelAllowed =
      mouseWheel === true ||
      (this.store.isVertical() && mouseWheel.vertical) ||
      (!this.store.isVertical() && mouseWheel.horizontal);
    if (!wheelAllowed) {
      return;
    }
    const delta = this.store.axisConf().wheelMainDelta(event);
    if (!delta) {
      return;
    }

    event.preventDefault();
    this.store.patch({
      lastTranslate: this.store.currentTranslate(),
    });
    const deltaMain = -delta * this.sensitivity;
    this.followUserMove(deltaMain, true);
  }
}

import { ElementRef, Injectable } from '@angular/core';
import { CarouselStore } from '../carousel.store';
import { CarouselTransformService } from './carousel-transform.service';
import { CarouselLoopService } from './carousel-loop.service';

@Injectable()
export class CarouselLayoutService {
  constructor(
    private readonly store: CarouselStore,
    private readonly transformService: CarouselTransformService,
    private readonly loopService: CarouselLoopService
  ) {}

  updateLayoutFromSlides(
    slidesEls: ReadonlyArray<ElementRef<HTMLElement>>
  ): boolean {
    if (!slidesEls.length) {
      return false;
    }

    const firstSize = this.store
      .axisConf()
      .rectSize(slidesEls[0]?.nativeElement);
    if (!firstSize) {
      return false;
    }

    this.store.patch({
      slidesElements: [...slidesEls],
    });

    this.store.resetSlidesIndexOrder();

    this.transformService.calculateInitialTranslations();
    this.loopService.initializeLoopCenter();

    return true;
  }
}

import { inject, Injectable, Injector, Renderer2 } from '@angular/core';
import { CarouselStore } from '../carousel.store';
import {
  CAROUSEL_VIEW,
  CarouselViewActions,
} from '../components/carousel/view-adapter';
import { extractVisibleSlides } from '../helpers/calculations.helper';
import { CAROUSEL_SLIDE_CLASS, SnapDom } from '../models/carousel.model';
import { extractSlidesFromContainer } from '../helpers/dom.helper';
import { positiveModulo } from '../helpers/utils.helper';

@Injectable()
export class CarouselLoopService {
  private store = inject(CarouselStore);
  private renderer = inject(Renderer2);
  private injector = inject(Injector);

  private get view(): CarouselViewActions {
    return this.injector.get(CAROUSEL_VIEW);
  }
  constructor() {}

  private get firstVisibleSlide() {
    return this.store.visibleDom()?.[0];
  }
  private get lastVisibleSlide() {
    return this.store.visibleDom()?.[this.store.visibleDom().length - 1];
  }

  private insertElement(before = true) {
    const slides = extractSlidesFromContainer(this.store.allSlides());

    const container = this.store.allSlides()?.nativeElement;
    const indexToInsert = before ? this.store.totalSlides() - 1 : 0;
    const index = this.store.slidesIndexOrder()[indexToInsert];
    const width = this.store.slidesWidths()[index];
    const elementToInsert = slides[indexToInsert] as HTMLElement | undefined;

    if (!elementToInsert) {
      return undefined;
    }

    if (before) {
      const firstReal = container.querySelector(
        '.' + CAROUSEL_SLIDE_CLASS
      ) as HTMLElement | null;
      if (!firstReal) {
        return undefined;
      }
      try {
        this.renderer.removeChild(container, elementToInsert);
        this.renderer.insertBefore(container, elementToInsert, firstReal);
      } catch (e) {
        this.renderer.insertBefore(container, elementToInsert, firstReal);
      }
    } else {
      this.renderer.appendChild(container, elementToInsert);
    }

    const slidesIndexOrder = this.store.slidesIndexOrder();

    const value = (
      before ? slidesIndexOrder.pop() : slidesIndexOrder.shift()
    ) as number;
    this.store.patch({
      slidesIndexOrder: before
        ? [value, ...slidesIndexOrder]
        : [...slidesIndexOrder, value],
    });

    const offset = before
      ? -this.store.slidesWidths()[value] - this.store.state().spaceBetween
      : this.store.slidesWidths()[value] + this.store.state().spaceBetween;

    this.applyTranslate(this.store.currentTranslate() + offset);

    this.store.patch({
      lastTranslate: this.store.lastTranslate() + offset,
    });

    return { width, offset };
  }

  /**
   * When moving slides manually.
   */
  private insertLoopSlidesByTranslation() {
    const [leftEdge, rightEdge] = [
      this.store.currentTranslate(),
      this.store.currentTranslate() + this.store.scrollWidth(),
    ];

    const viewportWidth = this.store.fullWidth();

    const buffer = 50;

    if (leftEdge > -buffer) {
      this.insertElement(true);
    }

    if (rightEdge < viewportWidth + buffer) {
      this.insertElement(false);
    }
  }

  /**
   * By prev or next button. Handle stepslides.
   * @param before
   */
  private insertLoopSlidesByNavigation(before = true) {
    const leftmostDom = this.firstVisibleSlide;
    const rightmostDom = this.lastVisibleSlide;
    const leftMostIndex = leftmostDom.domIndex;
    const rightMostIndex = rightmostDom.domIndex;
    const slidesAvailableBefore = leftMostIndex;
    const slidesAvailableAfter = this.store.totalSlides() - rightMostIndex - 1;

    if (
      before === true &&
      slidesAvailableBefore < this.store.state().stepSlides
    ) {
      const slidesToInsertBefore =
        this.store.state().stepSlides - slidesAvailableBefore;

      for (let i = 1; i <= slidesToInsertBefore; i++) {
        this.insertElement(before);
      }
    }
    if (
      before === false &&
      slidesAvailableAfter < this.store.state().stepSlides
    ) {
      const slidesToInsertAfter =
        this.store.state().stepSlides - slidesAvailableAfter;

      for (let i = 1; i <= slidesToInsertAfter; i++) {
        this.insertElement(before);
      }
    }
  }

  /**
   * Direct click on slide.
   * @param indexSlided
   */
  private insertLoopSlidesBySlidingTo(indexSlided: number) {
    const state = this.store.state();
    const futureTranslate = this.store.slideTranslates()[indexSlided];
    const currentTranslate = this.store.currentTranslate();
    const translateDiff = currentTranslate - futureTranslate;

    if (Math.abs(translateDiff) < 1) {
      return;
    }

    const modifiedVisibleDoms = extractVisibleSlides(
      this.store.snapsDom(),
      currentTranslate,
      state.fullWidth,
      translateDiff,
      this.store.center()
    );

    if (!modifiedVisibleDoms.length) {
      return;
    }

    const leftmostDom = modifiedVisibleDoms[0];
    const rightmostDom = modifiedVisibleDoms[modifiedVisibleDoms.length - 1];

    const leftEdge = leftmostDom.translate;
    const rightEdge = rightmostDom.translate + rightmostDom.width;
    const visibleSpan = Math.abs(rightEdge - leftEdge);

    const offset = state.fullWidth - visibleSpan;
    if (offset <= 0) {
      return;
    }

    const insertElement = (before = true) => {
      let mainOffset = offset;
      const firstInserted = this.insertElement(before);

      if (firstInserted) {
        mainOffset -= firstInserted.width + state.spaceBetween;

        let stop = false;
        while (mainOffset > 0 && !stop) {
          const inserted = this.insertElement(before);
          if (!inserted) {
            stop = true;
            continue;
          }
          mainOffset -= inserted.width + state.spaceBetween;
        }
      }
    };

    const shouldInsertBefore = leftmostDom.domIndex === 0;
    const shouldInsertAfter =
      rightmostDom.domIndex >= this.store.totalSlides() - 1;

    if (shouldInsertBefore) {
      insertElement(true);
    }

    if (shouldInsertAfter) {
      insertElement(false);
    }
  }

  /**
   * Determine if we must insert slide before or after.
   * 3 cases :
   * - From prev next action : insert as many slides as necessary (by step)
   * - From click on slide : insert as many slides as necessary (by space)
   * - From manual move : insert one slide at start or end
   * @returns
   */
  public insertLoopSlides(
    indexSlided: number | undefined = undefined,
    before?: boolean
  ) {
    if (!this.store.loop()) {
      return;
    }

    if (this.store.visibleDom().length === 0) {
      return;
    }

    // By manuel move.
    if (indexSlided === undefined && before === undefined) {
      this.insertLoopSlidesByTranslation();
      return;
    }

    // We slide to specific slide so we need to guess the future state of translation.
    if (indexSlided !== undefined) {
      this.insertLoopSlidesBySlidingTo(indexSlided);
      return;
    }

    // By clicking next or prev.
    this.insertLoopSlidesByNavigation(before);
  }

  private forceReflow() {
    const rect = this.store
      .state()
      .allSlides?.nativeElement?.getBoundingClientRect?.();
  }

  private applyTranslate(translate: number) {
    this.view.disableTransition();
    this.view.updateTransform(translate, false, true);
    this.forceReflow();
  }

  /**
   * Pre-populates the DOM for loop mode on initialization.
   * This is crucial for modes like 'center' to work from the start,
   * by ensuring the initial slide has slides before it in the DOM.
   */
  public initializeLoopCenter(): void {
    const state = this.store.state();

    if (!state.loop || !state.center) {
      return;
    }

    const totalSlides = this.store.totalSlides();
    if (totalSlides === 0 || !state.allSlides) {
      return;
    }

    const initialSlideWidth =
      this.store.slidesWidths()[state.initialSlide] ?? 0;
    const spaceToFill =
      state.fullWidth / 2 - initialSlideWidth / 2 - state.marginStart;

    if (spaceToFill <= 0) {
      return;
    }

    let widthFilled = 0;
    let slidesInserted = 0;

    while (widthFilled < spaceToFill && slidesInserted < totalSlides - 1) {
      const inserted = this.insertElement(true);

      if (inserted) {
        widthFilled += inserted.width + state.spaceBetween;
      }
      slidesInserted++;
    }
  }
}

import { inject, Injectable } from '@angular/core';
import { CarouselStore } from '../carousel.store';
import { positiveModulo } from '../helpers/utils.helper';
import { CarouselLoopService } from './carousel-loop.service';

@Injectable()
export class CarouselNavigationService {
  private readonly loopService = inject(CarouselLoopService);
  private readonly store = inject(CarouselStore);

  /**
   * Calculate new index after prev or next action.
   * @param isSlidingNext
   * @returns
   */
  public calculateNewPositionAfterNavigation(isSlidingNext = true) {
    if (this.store.navigateSlideBySlide()) {
      const newIndex = isSlidingNext
        ? this.store.currentRealPosition() + this.store.state().stepSlides
        : this.store.currentRealPosition() - this.store.state().stepSlides;
      return Math.max(0, Math.min(newIndex, this.store.totalSlides() - 1));
    }

    const newIndex = isSlidingNext
      ? this.store.currentPosition() + this.store.state().stepSlides
      : this.store.currentPosition() - this.store.state().stepSlides;

    if (this.store.loop()) {
      return positiveModulo(newIndex, this.store.totalSlides());
    }

    return Math.max(
      0,
      Math.min(
        newIndex,
        isSlidingNext
          ? this.store.lastSlideAnchor()
          : this.store.lastSlideAnchor() - 1
      )
    );
  }

  public isSlideDisabled(index: number): boolean {
    const slidesInput = this.store.slides();
    if (slidesInput && slidesInput.length) {
      return !!slidesInput[index]?.disabled;
    }

    const projected = this.store.state().projectedSlides ?? [];
    if (projected.length) {
      return !!projected[index]?.slideDisabled;
    }

    return false;
  }

  /**
   * Get next activated index from given index in given direction.
   */
  public findNextEnabledIndex(from: number, direction: 1 | -1): number {
    const total = this.store.totalSlides();
    if (!total) return from;

    let index = from;
    for (let i = 0; i < total; i++) {
      index = positiveModulo(index + direction, total);
      if (!this.isSlideDisabled(index)) {
        return index;
      }
    }

    return from;
  }

  public getNextIndex() {
    let indexSlided = undefined;
    this.loopService.insertLoopSlides(indexSlided, false);

    const hasReachedEnd = this.store.hasReachedEnd();
    let newPosition: number | undefined = undefined;
    if (hasReachedEnd && this.store.rewind()) {
      newPosition = 0;
    } else {
      newPosition = this.calculateNewPositionAfterNavigation(true);
    }

    newPosition = this.isSlideDisabled(newPosition)
      ? this.findNextEnabledIndex(this.store.currentPosition(), +1)
      : newPosition;

    return newPosition;
  }

  public getPrevIndex() {
    this.loopService.insertLoopSlides(undefined, true);

    let indexSlided = undefined;
    if (this.store.state().stepSlides > 1) {
      indexSlided =
        this.store.state().currentPosition -
        (this.store.state().stepSlides - 1) -
        1;
      indexSlided = positiveModulo(indexSlided, this.store.totalSlides());
    }

    const hasReachedStart = this.store.hasReachedStart();
    let newPosition: number | undefined = undefined;
    if (hasReachedStart && this.store.rewind()) {
      newPosition = this.store.totalSlides() - 1;
    } else {
      newPosition = this.calculateNewPositionAfterNavigation(false);
    }

    newPosition = this.isSlideDisabled(newPosition)
      ? this.findNextEnabledIndex(this.store.currentPosition(), -1)
      : newPosition;

    return newPosition;
  }
}

import { inject, Injectable, DestroyRef } from '@angular/core';
import { CarouselStore } from '../carousel.store';

interface InertiaConfig {
  baseFriction: number;
  edgeFriction: number;
  minVelocity: number;
}

@Injectable()
export class CarouselPhysicsService {
  private readonly store = inject(CarouselStore);
  private readonly destroyRef = inject(DestroyRef);

  private animationFrameId?: number;

  private readonly DEFAULT_CONFIG: InertiaConfig = {
    baseFriction: 0.93,
    edgeFriction: 0.8,
    minVelocity: 0.5,
  };

  constructor() {
    this.destroyRef.onDestroy(() => this.cancel());
  }

  applyInertia(
    config: InertiaConfig = this.DEFAULT_CONFIG,
    onUpdate?: (translate: number) => void
  ) {
    this.cancel();

    const step = () => {
      const velocity = this.store.state().velocity;

      if (Math.abs(velocity) < config.minVelocity) {
        return;
      }

      const friction = this.isAtEdge()
        ? config.edgeFriction
        : config.baseFriction;
      const newVelocity = velocity * friction;
      const newTranslate = this.store.currentTranslate() + velocity;

      const clampedTranslate = Math.max(
        this.store.state().maxTranslate,
        Math.min(newTranslate, this.store.state().minTranslate)
      );

      this.store.patch({
        currentTranslate: clampedTranslate,
        velocity: newVelocity,
      });

      onUpdate?.(clampedTranslate);

      this.animationFrameId = requestAnimationFrame(step);
    };

    this.animationFrameId = requestAnimationFrame(step);
  }

  cancel() {
    if (this.animationFrameId) {
      cancelAnimationFrame(this.animationFrameId);
      this.animationFrameId = undefined;
    }
  }

  private isAtEdge(): boolean {
    const translate = this.store.currentTranslate();
    return (
      translate <= this.store.state().maxTranslate ||
      translate >= this.store.state().minTranslate
    );
  }
}

