import { CommonModule } from '@angular/common';
import {
  Component,
  EventEmitter,
  OnInit,
  Input,
  Output,
  computed,
  input,
  inject,
} from '@angular/core';
import { CarouselStore } from '../../carousel.store';

type PaginationType = 'number' | 'dot' | 'dynamic_dot' | 'fraction';
export interface Pagination {
  type: PaginationType;
  clickable?: boolean;
  external?: boolean;
}

@Component({
  imports: [CommonModule],
  selector: 'app-pagination',
  templateUrl: './pagination.component.html',
  styleUrls: ['./pagination.component.scss'],
  host: {
    '[class.vertical]': 'this.store.isVertical()',
  },
})
export class PaginationComponent {
  public readonly store = inject(CarouselStore);

  @Output() goToSlide: EventEmitter<number> = new EventEmitter<number>();

  public readonly dotWidth = 0.5;

  constructor() {}

  public readonly maxSpan = computed(() => {
    if (this.store.totalSlidesVisible() >= 5) {
      return 5 * 0.5 + 4 * 1;
    }

    if (this.store.totalSlidesVisible() === 4) {
      return 4 * 0.5 + 3 * 1;
    }

    return 3 * 0.5 + 2 * 1;
  });

  public slideTo(slide: number) {
    this.goToSlide.emit(slide);
  }

  public readonly range = computed(() =>
    Array.from({ length: this.store.totalSlidesVisible() }, (_, i) => i + 1)
  );
  public readonly currentPositionVisible = computed(() => {
    const realPaginationPosition = Math.max(
      0,
      this.store.currentPosition() - this.store.firstSlideAnchor()
    );
    return Math.min(
      realPaginationPosition,
      this.store.totalSlidesVisible() - 1
    );
  });

  private computedMainOffset() {
    // maxwidth: 5 * 0.5rem + 4 * 1rem = 6.5

    // Default case : totalSlides >= 5
    // * - -   - * - -   - - * - -   - - * -  - - *
    if (this.store.totalSlidesVisible() >= 5) {
      return (
        (this.maxSpan() -
          (this.currentPositionVisible() * (this.maxSpan() - this.dotWidth)) /
            2 -
          this.dotWidth) /
        2
      );
    }
    // totalSlides = 4
    // maxwidth: 5
    // * - -   - * - -   - - * -   - - *
    // @todo
    if (this.store.totalSlidesVisible() === 4) {
      const middle = this.maxSpan() / 2;
      const firstLeft = middle - this.dotWidth / 2;
      const step = 1.5;
      return firstLeft - step * this.currentPositionVisible();
      // (
      //   (this.maxWidth() -
      //     (this.currentPositionVisible * (this.maxWidth() - 0.5)) / 2 -
      //     0.5) /
      //   2
      // );
    }

    // totalSlides = 3
    // maxWidth: 3.5
    //  * -    - * -    - *
    if (this.store.totalSlidesVisible() === 3) {
      return (
        (this.maxSpan() -
          this.currentPositionVisible() * (this.maxSpan() - this.dotWidth) -
          this.dotWidth) /
        2
      );
    }

    return 0;
  }

  public readonly dotMainOffset = computed(() => {
    const offset = this.computedMainOffset();
    return this.store.isRtl() ? -offset : offset;
  });
}

styleUrlsData.data:
:host {
  user-select: none;
  margin-bottom: 1rem;
  &.vertical {
    margin-bottom: 0;
  }
}

:host(.vertical) .dot-pagination.pagination-dot,
:host(.vertical) .dot-pagination.dynamic {
  flex-direction: column;
}

.dot-pagination {
  &.clickable {
    .dot {
      cursor: pointer;
    }
  }

  .dot {
    opacity: 0.5;
    width: 0.5rem;
    height: 0.5rem;
    background-color: grey;
    //border: 1px solid grey;
    border-radius: 50%;

    &.selected {
      background-color: darkblue;
      transform: scale(1.5);
    }
  }

  &.pagination-dot {
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: center;
    gap: 1rem;
    padding: 1rem;

    &.rtl {
      flex-direction: row-reverse;
    }
  }

  &.dynamic {
    display: flex;
    flex-direction: row;
    gap: 1rem;
    overflow: hidden;

    .dot {
      position: relative;
      transition: left 0.2s, top 0.2s;
      flex: 0 0 auto;
      transform: scale(1);

      &.next,
      &.prev {
        transform: scale(0.6);
      }

      &.next-next,
      &.prev-prev {
        transform: scale(0.4);
      }
    }
  }
}

